# This file is used to set the paths to specific required resources on your system, and for
# certain options during the variant call - essentially stuff that will generally be unchanged most of the time

# the following need to be defined:
# the full path to the reference sequence to be used - without extension. The .fasta and .dict files for this reference should be found in this location
if [ -z ${ASSEMBLY} ]; then
	ASSEMBLY=hg38
fi
if [ "${ASSEMBLY}" == "hg38" ]; then
	export REF="/resource/bundles/hg38/GCA_000001405.15_GRCh38/GCA_000001405.15_GRCh38_no_alt_analysis_set"
	#export REF="/resource/bundles/hg38/GCA_000001405.15_GRCh38/GCA_000001405.15_GRCh38_no_alt_plus_hs38d1_analysis_set_nodecoyandnounlocalised"
	export REFA=${REF}.fna
	export REFD=${REF}.dict
	# the co-ordinates for the true x region for the above reference - should be found in the Genome Reference Consortium site
	export TRUEX="chrX:2781480-155701382"
	export XPAR1="chrX:10001-2781479"
	export XPAR2="chrX:155701383-156030895"
	# the dbsnp data to be used (providing variant ID and for VQSR)
	export DBSNP="/resource/bundles/dbSNP/hg38/dbSNP_155_hg38.vcf.gz"
	# the hap map data to use (for VQSR)
	export hapmapversion="/resource/bundles/hg38/hg38BroadOldbundle/hapmap_3.3.hg38.vcf.gz"
	# the omni data to use (for VQSR)
	export omniversion="/resource/bundles/hg38/hg38BroadOldbundle/1000G_omni2.5.hg38.vcf.gz"
	# the 1000 genomes data to use (for VQSR)
	export KGversion="/resource/bundles/hg38/hg38BroadOldbundle/1000G_phase1.snps.high_confidence.hg38.vcf.gz"
	# the high quality indel data to use (for VQSR)
	export millsversion="/resource/bundles/hg38/hg38BroadOldbundle/Mills_and_1000G_gold_standard.indels.hg38.vcf.gz"
	# population data for calculating genotype posteriors
	export popdata="/resource/bundles/gnomAD_Bulk/3.1.2/gnomad.genomes.v3.1.2.sites.vcf.gz"
	# path to the directory holding the bed files for specific capture platforms.
	export PLATFORMS="/resource/bundles/Capture_Platforms/hg38"
	# the following variable determines how the X chromosome is processed. If it is "whole" then it will not be split up for the genotype refinement, and there will be no calculation of genotype posteriors or de novos for any of the X chromosome. This is essential if there are any X chromosome aneusomies in the cohort (eg XXX, XXY, X0) as those tools only work on disomic data. If this variable is set to anything else the XPAR regions will be processed through the full genotype refinement, but the True X will not
	export XPROC="whole"
	# if set to allelespecific="yes" then allele specific annotations will be used VQSR, if set to anything else they will not be allele specific
	export allelespecific="yes"
